const sdl = require('../../sdl')

const GuaObject = require('../gua_object')
const Color = require('../color')

const log = require('../utils').log

class Line extends GuaObject {
    constructor(position, color=Color.black()) {
        super()
        this.setup(position, color)
    }
    setup(position, color) {
        const self = this
        self.position = position
        self.color = color
    }
    __added(view) {
        super.__added(view)
    }
    draw() {
        const self = this
        let renderer = self.renderer
        let [x1, y1, x2, y2] = self.position
        let [r, g, b, a] = self.color
        sdl.SDL_SetRenderDrawColor(renderer, r, g, b, a)
        sdl.SDL_RenderDrawLine(renderer, x1, y1, x2, y2)
    }
}

module.exports = Line
