const sdl = require('../../sdl')

const GuaObject = require('../gua_object')
const Color = require('../color')

const log = require('../utils').log

class Rect extends GuaObject {
    constructor(config, color=Color.black()) {
        super()
        this.setup(config, color)
    }
    setup(config, color) {
        const self = this
        self.config = config
        self.color = color
    }
    __added(view) {
        super.__added(view)
    }
    draw() {
        const self = this
        let config = self.config
        let color = self.color
        self.drawRect(config, color)
    }
}

module.exports = Rect
