const Key = require('./keycode')

const log = console.log.bind(console)

const keyNameByCode = function(keyCode) {
    let keys = Object.keys(Key)
    for (let i = 0; i < keys.length; i++) {
        let keyName = keys[i]
        let code = Key[keyName]
        if (code === keyCode) {
            return keyName
        }
    }
}

module.exports = {
    log,
    keyNameByCode,
}
